import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [city, setCity] = useState('');
  const [weather, setWeather] = useState(null);
  const [error, setError] = useState('');
  const API_KEY = '414304b6c8cb26469c0b5031b0da6e33';  

  const indianCityRegex = /^[a-zA-Z\s]{2,50}$/;

  useEffect(() => {
    if (city && indianCityRegex.test(city)) {
      fetchWeatherData();
    }
  }, [city]);

  const fetchWeatherData = async () => {
    try {
      const response = await fetch(
        `https://api.openweathermap.org/data/2.5/weather?q=${city},IN&appid=${API_KEY}&units=metric`
      );
      const data = await response.json();
      
      if (data.cod === 200) {
        setWeather(data);
        setError('');
      } else {
        setError('City not found. Please enter a valid Indian city name.');
        setWeather(null);
      }
    } catch (err) {
      setError('Failed to fetch weather data. Please try again.');
      setWeather(null);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!indianCityRegex.test(city)) {
      setError('Please enter a valid Indian city name (letters and spaces only)');
      return;
    }
    fetchWeatherData();
  };

  return (
    <div className="app">
      <div className="container">
        <h1>Indian Weather App</h1>
        
        <form onSubmit={handleSubmit} className="search-form">
          <input
            type="text"
            value={city}
            onChange={(e) => setCity(e.target.value)}
            placeholder="Enter Indian city name..."
            className="search-input"
          />
          <button type="submit" className="search-button">
            Search
          </button>
        </form>

        {error && <div className="error">{error}</div>}

        {weather && (
          <div className="weather-info">
            <h2>{weather.name}</h2>
            <div className="weather-details">
              <p className="temperature">{Math.round(weather.main.temp)}°C</p>
              <p className="description">{weather.weather[0].description}</p>
              <div className="additional-info">
                <p>Humidity: {weather.main.humidity}%</p>
                <p>Wind Speed: {weather.wind.speed} m/s</p>
                <p>Feels Like: {Math.round(weather.main.feels_like)}°C</p>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
